// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract BridgePool  is Ownable, Pausable {
    using SafeERC20 for IERC20;

    IERC20 private immutable TOKEN;
    uint256 public totalAmount;
    mapping (address => LiquidityData[]) public liquidityData;
    uint256[4] private rewardRate = [10, 15, 20, 25]; // rate = rewardRate /1000

    struct LiquidityData {
        uint256 amount;
        uint256 startTime;
    }

    constructor(address _token) {
        TOKEN = IERC20(_token);
    }

    function addLiquidity(uint256 _amount) external whenNotPaused{
        TOKEN.safeTransferFrom(tx.origin, address(this), _amount);
        totalAmount += _amount;
        liquidityData[tx.origin].push(LiquidityData(_amount, block.timestamp));
    }

    function removeLiquidity(uint256 _amount, uint256 _index) external whenNotPaused{
        require(_index <= liquidityData[tx.origin].length, "index must be less than times of user's liquidity");
        require(_amount <= liquidityData[tx.origin][_index].amount, "amount must be less than user's liquidity amount"); 

        liquidityData[tx.origin][_index].amount -= _amount;
        uint256 rate = rewardRate[_getRateReward(liquidityData[tx.origin][_index].startTime)];
        TOKEN.transfer(tx.origin, _amount * (1000 + rate) / 1000);

    }

    function _getRateReward(uint256 _startTime) private view returns(uint256) {
        uint256 time = block.timestamp - _startTime;
        if (time < 7884000) {
            return 0;
        }
        if (time < 15768000) {
            return 1;
        }
        if (time < 23652000) {
            return 2;
        }
        return 3;
    }

    function changePauseState(bool state) external onlyOwner {
        if (state) {
            _pause();
            return;
        }

        _unpause();
    }
}

